package Program;

public class Strings {

	public static void main(java.lang.String[] args) {
		// TODO Auto-generated method stub
		//String is an object that represents sequence of character
		
		//String literals
		String a = "Vicky";
		String a1 = "Vicky1";
		
		//New memory locate operator
		
		//Forward print character
	
		String s0 = new String("My name is kumar vicky");
		for(int i = 0;i<s0.length();i++)
		{
			System.out.println(s0.charAt(i));
		}
		
		//print character in reverse order
		
		String s1 = new String("My name is kumar vicky");
		for(int i = s1.length()-1;i<s1.length();i--)
		{
			System.out.println(s1.charAt(i));
		}
		
		
		

		
		
		
		
		
		
		
		

	}

}
